public class TempCodeUtils { public static int findMaxValue(int[] array) { if (array == null || array.length == 0) { throw new IllegalArgumentException("Array must not be null or empty"); } int maxValue = array[0]; for (int value : array) { if (value > maxValue) { maxValue = value; } } return maxValue; }public static void main(String[] args) {int result = findMaxValue(new int[]{1, 2}, 4, 1);System.out.println(result);}}